export const initialState = {
  cart: [],
};

const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_CART":
      return {
        ...state,
        items: [...items, action.item],
      };

    default:
      return state;
  }
};

export default reducer;
